import jxl.*

def email
def firstname
def lastname
String filepath="C:/Users/NagarathnaBalakrishn/Desktop/SampleData1.xls"
File Excel = new File(filepath)
Workbook wb = Workbook.getWorkbook(Excel)
Sheet sh = wb.getSheet("Sheet1")

def rowsize = sh.getRows().toInteger()
def colsize = sh.getColumns().toInteger()

log.info "Total no of Rows are: " + rowsize

log.info "Total no of columns are: " + colsize


for( def i=0;i<rowsize;i++)
{
	for (def j=0;j<colsize;j++)
	{

		if (j==0)
		{
			 email=sh.getCell(j,i).getContents()
		}
		if (j==1)
		{
			 firstname=sh.getCell(j,i).getContents()
		}
		else
		{
			lastname=sh.getCell(j,i).getContents()
		}
	}


def tc =testRunner.testCase.testSuite.project.testSuites["customerCustomerRepositoryV1_ETE_Functional_Testing"].testCases["ETE_Functional_TestCase_Property_Transfer"]

tc.testSteps["Datasetup"].setPropertyValue("email",email)
tc.testSteps["Datasetup"].setPropertyValue("firstname",firstname)
tc.testSteps["Datasetup"].setPropertyValue("lastname",lastname)

log.info "email is $email"
log.info "firstname is $firstname"
log.info "lastname is $lastname"


def runner = tc.run(null,false)
log.info "Number of steps executed" + runner.results.size()

